CREATE PROCEDURE sp_transform_usd_to_eur8
    @api_data NVARCHAR(MAX),  -- JSON string
    @upload_dt DATETIME       -- Timestamp
AS
BEGIN
    -- Debugging: Print the raw JSON input
    PRINT 'Raw JSON:';
    PRINT @api_data;

    -- Check if JSON is valid
    IF ISJSON(@api_data) = 1
    BEGIN
        PRINT 'Valid JSON';

        -- Extract and print specific values for debugging
        DECLARE @usd_rate FLOAT = JSON_VALUE(@api_data, '$.USD');
        DECLARE @eur_rate FLOAT = JSON_VALUE(@api_data, '$.EUR');

        PRINT 'USD Rate: ' + CAST(@usd_rate AS NVARCHAR(50));
        PRINT 'EUR Rate: ' + CAST(@eur_rate AS NVARCHAR(50));

        -- Insert transformed data
        INSERT INTO source_data_EUR (upload_dt, Currency, rate)
        SELECT 
            @upload_dt AS upload_dt,
            [key] AS Currency,                       -- Extract currency codes
            CAST([value] AS FLOAT) / @eur_rate AS rate -- Convert to EUR
        FROM OPENJSON(@api_data)                    -- Parse JSON key-value pairs
        WHERE [key] <> 'EUR';                       -- Exclude EUR as it's the base
    END
    ELSE
    BEGIN
        THROW 50000, 'Invalid JSON format in api_data.', 1;
    END;
END;

source.sqlReaderQuery:
SELECT TOP 1 api_data, upload_dt
FROM source_data_USD
ORDER BY upload_dt DESC;
